// Extract and format transcript text
const transcriptResponse = $input.item.json;
let transcriptText = '';

// The API returns {transcription: "text"}
if (transcriptResponse && transcriptResponse.transcription) {
  transcriptText = transcriptResponse.transcription;
} else {
  throw new Error('Unable to extract transcript from the response');
}

// Get video info from webhook data
let webhookData;
try {
  const webhookNode = $node["Webhook"].json;
  if (Array.isArray(webhookNode)) {
    webhookData = webhookNode[0].body || webhookNode[0];
  } else {
    webhookData = webhookNode.body || webhookNode;
  }
} catch (e) {
  webhookData = { youtubeUrl: '', videoId: '' };
}

return {
  transcript: transcriptText,
  youtubeUrl: webhookData.youtubeUrl || '',
  videoId: webhookData.videoId || '',
  transcriptLength: transcriptText.length
};

// Get data from previous nodes with error handling
let videoData, channelData;

try {
  // Check if video data exists (handle array response)
  const videoResponse = $node["Get Video Details"].json;
  if (Array.isArray(videoResponse) && videoResponse[0]) {
    videoData = videoResponse[0].items[0];
  } else if (videoResponse && videoResponse.items && videoResponse.items[0]) {
    videoData = videoResponse.items[0];
  } else {
    throw new Error("No video data found");
  }
} catch (e) {
  return {
    error: "Unable to get video data",
    message: e.message
  };
}

try {
  // Check if channel data exists (handle array response)
  const channelResponse = $node["Get Channel Details"].json;
  if (Array.isArray(channelResponse) && channelResponse[0]) {
    channelData = channelResponse[0].items[0];
  } else if (channelResponse && channelResponse.items && channelResponse.items[0]) {
    channelData = channelResponse.items[0];
  } else {
    throw new Error("No channel data found");
  }
} catch (e) {
  return {
    error: "Unable to get channel data",
    message: e.message
  };
}

// Calculate earnings estimates
const views = parseInt(videoData.statistics.viewCount) || 0;
const likes = parseInt(videoData.statistics.likeCount) || 0;
const comments = parseInt(videoData.statistics.commentCount) || 0;

const channelViews = parseInt(channelData.statistics.viewCount) || 0;
const subscriberCount = parseInt(channelData.statistics.subscriberCount) || 0;
const videoCount = parseInt(channelData.statistics.videoCount) || 1;

// CPM (Cost Per Mille) estimates based on channel size
let estimatedCPM = 2; // Default $2 CPM

if (subscriberCount > 1000000) {
  estimatedCPM = 7; // Large channels get better rates
} else if (subscriberCount > 100000) {
  estimatedCPM = 5;
} else if (subscriberCount > 10000) {
  estimatedCPM = 3;
}

// Video earnings calculation
const videoEarnings = {
  low: (views / 1000) * (estimatedCPM * 0.5),
  mid: (views / 1000) * estimatedCPM,
  high: (views / 1000) * (estimatedCPM * 2)
};

// Channel monthly earnings (rough estimate)
const avgMonthlyViews = channelViews / 12;
const monthlyEarnings = {
  low: (avgMonthlyViews / 1000) * (estimatedCPM * 0.5),
  mid: (avgMonthlyViews / 1000) * estimatedCPM,
  high: (avgMonthlyViews / 1000) * (estimatedCPM * 2)
};

// Engagement metrics
const engagementRate = views > 0 ? ((likes + comments) / views * 100).toFixed(2) : "0";

return {
  channelId: channelData.id,
  videoAnalytics: {
    views: views.toLocaleString(),
    likes: likes.toLocaleString(),
    comments: comments.toLocaleString(),
    engagementRate: engagementRate + '%',
    estimatedEarnings: {
      low: '$' + videoEarnings.low.toFixed(2),
      mid: '$' + videoEarnings.mid.toFixed(2),
      high: '$' + videoEarnings.high.toFixed(2),
      note: 'Based on estimated CPM of $' + estimatedCPM
    }
  },
  channelAnalytics: {
    name: channelData.snippet.title || 'Unknown',
    description: (channelData.snippet.description || '').substring(0, 200) + '...',
    country: channelData.snippet.country || 'Not specified',
    customUrl: channelData.snippet.customUrl || '',
    publishedAt: channelData.snippet.publishedAt ? new Date(channelData.snippet.publishedAt).toLocaleDateString() : 'Unknown',
    subscribers: subscriberCount.toLocaleString(),
    totalViews: channelViews.toLocaleString(),
    totalVideos: videoCount.toLocaleString(),
    averageViewsPerVideo: Math.round(channelViews / videoCount).toLocaleString(),
    estimatedMonthlyEarnings: {
      low: '$' + monthlyEarnings.low.toFixed(2),
      mid: '$' + monthlyEarnings.mid.toFixed(2),
      high: '$' + monthlyEarnings.high.toFixed(2),
      note: 'Rough estimate based on total channel views'
    },
    channelKeywords: channelData.brandingSettings?.channel?.keywords || 'Not specified',
    featuredChannels: channelData.brandingSettings?.channel?.featuredChannelsUrls?.length || 0
  }
};

// Get data from previous nodes safely
let deepseekResponse, videoInfo, analyticsData, transcriptData;

try {
  deepseekResponse = $node["DeepSeek API"].json;
  if (Array.isArray(deepseekResponse)) {
    deepseekResponse = deepseekResponse[0];
  }
} catch (e) {
  deepseekResponse = null;
}

try {
  videoInfo = $node["Get Video Details"].json;
  if (Array.isArray(videoInfo)) {
    videoInfo = videoInfo[0];
  }
} catch (e) {
  videoInfo = null;
}

try {
  analyticsData = $node["Calculate Analytics & Earnings"].json;
  if (Array.isArray(analyticsData)) {
    analyticsData = analyticsData[0];
  }
} catch (e) {
  analyticsData = null;
}

try {
  transcriptData = $node["Format Transcript"].json;
  if (Array.isArray(transcriptData)) {
    transcriptData = transcriptData[0];
  }
} catch (e) {
  transcriptData = null;
}

// Debug logging
console.log('Raw analyticsData:', analyticsData);
console.log('Has videoAnalytics?', analyticsData?.videoAnalytics);
console.log('Has channelAnalytics?', analyticsData?.channelAnalytics);

// Extract summary
let summary = '';
if (deepseekResponse && deepseekResponse.choices && deepseekResponse.choices[0]) {
  summary = deepseekResponse.choices[0].message.content;
} else {
  summary = 'Summary generation failed';
}

// Build response with defaults
const response = {
  success: true,
  youtubeUrl: transcriptData ? transcriptData.youtubeUrl : '',
  videoId: transcriptData ? transcriptData.videoId : '',
  timestamp: new Date().toISOString(),
  summary: summary
};

// Try to add video details
if (videoInfo && videoInfo.items && videoInfo.items[0]) {
  const videoItem = videoInfo.items[0];
  response.title = videoItem.snippet.title || 'Unknown Title';
  response.channel = videoItem.snippet.channelTitle || 'Unknown Channel';
  response.publishedAt = videoItem.snippet.publishedAt ? new Date(videoItem.snippet.publishedAt).toLocaleDateString() : 'Unknown';

  // Parse duration
  if (videoItem.contentDetails && videoItem.contentDetails.duration) {
    const isoDuration = videoItem.contentDetails.duration;
    const match = isoDuration.match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
    if (match) {
      const hours = parseInt(match[1]) || 0;
      const minutes = parseInt(match[2]) || 0;
      const seconds = parseInt(match[3]) || 0;

      if (hours > 0) {
        response.duration = `${hours}h ${minutes}m ${seconds}s`;
      } else if (minutes > 0) {
        response.duration = `${minutes}m ${seconds}s`;
      } else {
        response.duration = `${seconds}s`;
      }
    } else {
      response.duration = 'Unknown';
    }
  }

  // Add tags
  response.tags = videoItem.snippet.tags || [];
}

// IMPORTANT FIX: Only add analytics if it's actually available and not an error
if (analyticsData && analyticsData.videoAnalytics && analyticsData.channelAnalytics) {
  // Analytics data is available
  response.analytics = {
    video: analyticsData.videoAnalytics,
    channel: analyticsData.channelAnalytics
  };
} else {
  // No analytics data or error occurred
  response.analytics = {
    video: {},
    channel: {},
    note: analyticsData?.error || "Analytics data not available"
  };
}

// More debug logging
console.log('Final response analytics:', response.analytics);

return response;

