const thumbnailUrl = videoId ? `https://img.youtube.com/vi/${videoId}/maxresdefault.jpg` : '';

// Parse the AI-generated title and content from the summary
let redditTitle = '';
let redditContent = '';

// Extract title - handle asterisks and formatting
const titleMatch = summary.match(/\*{0,2}TITLE:\s*(.+?)(?:\*{0,2})\s*(?:\n|$)/);

if (titleMatch) {
  // Clean the title - remove all asterisks
  redditTitle = titleMatch[1].replace(/\*+/g, '').trim();
} else {
  redditTitle = `Check out this ${channel} video!`;
}

// Extract content - get everything after the TITLE line
const contentSplit = summary.split(/\*{0,2}TITLE:.*?\*{0,2}\s*\n+/);
if (contentSplit.length > 1) {
  redditContent = contentSplit[1].trim();
} else {
  // If split didn't work, try to find where content starts
  const contentMatch = summary.match(/CONTENT:\s*([\s\S]+)/);
  if (contentMatch) {
    redditContent = contentMatch[1].trim();
  } else {
    // Last resort - use summary but remove title line
    redditContent = summary.replace(/\*{0,2}TITLE:.*?\*{0,2}\s*\n+/, '').trim();
  }
}

