console.log("Cleaned Reddit Content:", redditContent.substring(0, 100) + "...");

// Return formatted content
return {
  videoId: videoId,
  youtubeUrl: youtubeUrl,
  title: originalTitle,
  channel: channel,
  summary: summary,
  thumbnailUrl: thumbnailUrl,
  socialMedia: {
