const deepseekResponse = $json;
let aiContent = '';

// Extract the AI response
if (deepseekResponse && deepseekResponse.choices && deepseekResponse.choices[0]) {
  aiContent = deepseekResponse.choices[0].message.content;
} else {
  throw new Error('No response from DeepSeek');
}

// Debug log
console.log("Raw AI content:", aiContent);

// Parse title - handle various formats with or without asterisks
const titleRegex = /\*{0,2}TITLE:\s*(.+?)(?:\*{0,2})\s*$/m;
const titleMatch = aiContent.match(titleRegex);

let redditTitle = 'Check out this cool video I found';
let redditContent = aiContent;

if (titleMatch) {
  // Clean the title - remove asterisks and extra whitespace
  redditTitle = titleMatch[1].replace(/\*+/g, '').trim();
}

// Extract content - everything after the TITLE line
const contentSplit = aiContent.split(/\*{0,2}TITLE:.*?\*{0,2}\s*\n+/);
if (contentSplit.length > 1) {
  redditContent = contentSplit[1].trim();

  // If content starts with CONTENT:, remove it
  redditContent = redditContent.replace(/^CONTENT:\s*/i, '');

  // Remove any duplicate title line in the content
  redditContent = redditContent.replace(/^\*{0,2}TITLE:.*?\*{0,2}\s*\n+/i, '');

  // Clean up any remaining asterisks at the start
  redditContent = redditContent.replace(/^\*+\s*/g, '');
}

// Final cleanup - ensure no formatting artifacts
redditTitle = redditTitle.replace(/\*+$/, '').trim(); // Remove trailing asterisks
redditContent = redditContent.trim();

console.log("Parsed title:", redditTitle);
console.log("Parsed content:", redditContent);

// Pass through all data plus parsed content
return {
  ...$json,
  parsedTitle: redditTitle,
  parsedContent: redditContent,
  rawAiResponse: aiContent
};

