// Get all searches
const allSearches = $input.all();
const allJobs = [];
const errors = [];
const BATCH_SIZE = 5; // Process 5 searches at once
const DELAY_BETWEEN_BATCHES = 1000; // 1 second

console.log(`Processing ${allSearches.length} searches in batches of ${BATCH_SIZE}...`);

// Helper function to process a single search
async function processSearch(search, index) {
  try {
    const searchTermsKey = Object.keys(search).find(key => key.includes("Search") && key.includes("Terms"));
    const searchTerms = searchTermsKey ? search[searchTermsKey] : "";
    const location = search["Location & Mileage Radius"] || "";

    if (!searchTerms) return [];

    const locationParts = location.split(',');
    const zipcode = locationParts[0]?.trim() || "40502";
    const miles = locationParts[1]?.trim().split(' ')[0] || "100";

    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: 'https://api.adzuna.com/v1/api/jobs/us/search/1',
      qs: {
        app_id: '27f15cf6',
        app_key: 'xxxxxxx',
        results_per_page: 20, // Reduced to speed up
        what: searchTerms,
        where: zipcode,
        distance: miles
      },
      timeout: 5000
    });

    const jobs = [];
    if (response.results) {
      response.results.forEach(job => {
        jobs.push({
  title: job.title || "",
  company: job.company?.display_name || "",
  location: job.location?.display_name || "",
  salary: job.salary_min || null,
  easyApply: false,  // Add this back
  url: job.redirect_url || "",
  summary: job.description || "",
  postDate: job.created ? job.created.split('T')[0] : "",
  isAd: false,  // Add this back
  jobKey: job.id || "",
  isSponsored: false,  // Add this back
  searchCategory: search["[\"Industry Name\"]"] || searchTerms.substring(0, 30)
});
      });
    }
    return jobs;
  } catch (error) {
    errors.push({ search: index, error: error.message });
    return [];
  }
}

// Process in batches
for (let i = 0; i < allSearches.length; i += BATCH_SIZE) {
  const batch = allSearches.slice(i, i + BATCH_SIZE);
  console.log(`Processing batch ${Math.floor(i/BATCH_SIZE) + 1} of ${Math.ceil(allSearches.length/BATCH_SIZE)}`);

  // Process batch in parallel
  const batchPromises = batch.map((search, idx) => 
    processSearch(search.json, i + idx)
  );

  const batchResults = await Promise.all(batchPromises);

  // Flatten and add to allJobs
  batchResults.forEach(jobs => allJobs.push(...jobs));

  // Delay between batches (except for last batch)
  if (i + BATCH_SIZE < allSearches.length) {
    await new Promise(resolve => setTimeout(resolve, DELAY_BETWEEN_BATCHES));
  }
}

// Remove duplicates
const uniqueJobs = [];
const seenKeys = new Set();

allJobs.forEach(job => {
  if (!seenKeys.has(job.jobKey)) {
    seenKeys.add(job.jobKey);
    uniqueJobs.push(job);
  }
});

// Group by category for summary
const jobsByCategory = {};
uniqueJobs.forEach(job => {
  if (!jobsByCategory[job.searchCategory]) {
    jobsByCategory[job.searchCategory] = 0;
  }
  jobsByCategory[job.searchCategory]++;
});

return [{
  json: {
    jobs: uniqueJobs,
    totalJobs: uniqueJobs.length,
    searchesProcessed: allSearches.length,
    jobsByCategory: jobsByCategory,
    errors: errors,
    processingTime: `~${Math.ceil(allSearches.length/BATCH_SIZE) * (DELAY_BETWEEN_BATCHES/1000)} seconds`
  }
}];

// Get the job results and exclusion lists
const results = $input.item.json.results || [];
const keywordExclusions = $node["Code"].json.keywordExclusions || "";
const locationExclusions = $node["Code"].json.locationExclusions || "";

// Convert exclusions to arrays and clean them up
const excludedKeywords = keywordExclusions.split(',').map(k => k.trim().toLowerCase()).filter(k => k);

// For testing, only exclude states and major cities, not counties
const majorExclusions = ["ohio", "indiana", "west virginia", "tennessee", "louisville", "cincinnati"];
const excludedLocations = locationExclusions.split(',')
  .map(l => l.trim().toLowerCase())
  .filter(l => majorExclusions.includes(l.toLowerCase()));

// Filter jobs
const filteredJobs = results.filter(job => {
  const jobTitle = (job.title || "").toLowerCase();
  const jobDescription = (job.description || "").toLowerCase();
  const jobLocation = (job.location?.display_name || "").toLowerCase();

  // Check keyword exclusions in title only (not description for now)
  const hasExcludedKeyword = excludedKeywords.some(keyword => 
    jobTitle.includes(keyword)
  );

  // Check location exclusions
  const hasExcludedLocation = excludedLocations.some(location => 
    jobLocation.includes(location)
  );

  // Return true if job should be kept (no exclusions found)
  return !hasExcludedKeyword && !hasExcludedLocation;
});

// Return the filtered results
return {
  results: filteredJobs,
  originalCount: results.length,
  filteredCount: filteredJobs.length
};

// Get filtered results - make sure we get the array
const results = $input.item.json.results || [];
const formattedJobs = [];

for (const job of results) {
  formattedJobs.push({
    title: job.title || "",
    company: job.company?.display_name || "",
    location: job.location?.display_name || "",
    salary: job.salary_min || null,
    easyApply: false,
    url: job.redirect_url || "",
    summary: job.description || "",
    postDate: job.created ? job.created.split('T')[0] : "",
    isAd: false,
    jobKey: job.id || "",
    isSponsored: false
  });
}

// Return the array of jobs, not just one
return formattedJobs;  // This returns the array directly

// Get all items from the input
const allItems = $input.all();
const allJobs = [];

// Extract jobs from each item
allItems.forEach(item => {
  // Check if the item contains an array of jobs or a single job
  const data = item.json;

  if (Array.isArray(data)) {
    // It's already an array of jobs
    allJobs.push(...data);
  } else if (data.jobs) {
    // It has a jobs property
    if (Array.isArray(data.jobs)) {
      allJobs.push(...data.jobs);
    } else {
      allJobs.push(data.jobs);
    }
  } else if (data.title) {
    // It's a single job object
    allJobs.push(data);
  }
});

// Remove duplicates based on jobKey
const uniqueJobs = [];
const seenKeys = new Set();

allJobs.forEach(job => {
  if (job?.jobKey && !seenKeys.has(job.jobKey)) {
    seenKeys.add(job.jobKey);
    uniqueJobs.push(job);
  }
});

return [{
  json: {
    jobs: uniqueJobs,
    totalJobs: uniqueJobs.length,
    timestamp: new Date().toISOString()
  }
}];
